#include <stdio.h>
#include <stdlib.h>

// Problem : What is the biggest multiplication of 13 sequence digits						

unsigned long long int getNum(char);

int main(void){
	// The 1000-digit number is under this expression
	char numbers[] = "";
	unsigned long long int max = 0; // max that is on unsigned long long int type, save the maximum multiplication of 13 sequence digit. It's starting value is 0 because of smallest positive value.
	int i,j; // i and j is used for indis in for loops
	int cont = 13; // Question asked 13 sequence digit's multiplication but we would like to have flexible program.	
	for(i=0; i<(sizeof(numbers)-cont);i++){
		unsigned long long int tmp = 1;
		for (j=0 ; j<cont; j++){
			tmp *= getNum(numbers[i+j]);
		}
		if(tmp > max){
			max = tmp;
		}
	}
	
	printf("Max value : %lld\n",max);
	return 0;
}

unsigned long long int getNum(char num){	// This function get a char as a parameter and it calculates the difference of parameter's ascii value and '0' ascii value.
	unsigned long long int result = num - '0';
	return result;	// It returns that difference value.
}
